// Estado persistente para evitar repeticiones
const state = $getWorkflowStaticData('global');
if (!state.usedProducts) state.usedProducts = [];
if (!state.usedImages) state.usedImages = [];
if (!state.usedTypes) state.usedTypes = [];
if (!state.articleCount) state.articleCount = 0;

// Seleccionar producto sin repetir
let availableProducts = CONFIG.products.filter(p => !state.usedProducts.includes(p.id));
if (availableProducts.length === 0) {
  state.usedProducts = [];
  availableProducts = CONFIG.products;
}
const product = availableProducts[Math.floor(Math.random() * availableProducts.length)];
state.usedProducts.push(product.id);

// Seleccionar tipo de articulo sin repetir
let availableTypes = CONFIG.articleTypes.filter(t => !state.usedTypes.includes(t.id));
if (availableTypes.length === 0) {
  state.usedTypes = [];
  availableTypes = CONFIG.articleTypes;
}
const articleType = availableTypes[Math.floor(Math.random() * availableTypes.length)];
state.usedTypes.push(articleType.id);

// Seleccionar imagen sin repetir
let categoryImages = CONFIG.images[product.category] || CONFIG.images['seguridad-contra-incendios'];
let availableImages = categoryImages.filter(i => !state.usedImages.includes(i));
if (availableImages.length === 0) {
  state.usedImages = state.usedImages.filter(i => !categoryImages.includes(i));
  availableImages = categoryImages;
}
const heroImage = availableImages[Math.floor(Math.random() * availableImages.length)];
state.usedImages.push(heroImage);

// Seleccionar colonias y zonas aleatorias
const colonias = [...CONFIG.colonias].sort(() => Math.random() - 0.5).slice(0, 6);
const zonasIndustriales = [...CONFIG.zonasIndustriales].sort(() => Math.random() - 0.5).slice(0, 3);

// Incrementar contador
state.articleCount++;

// Generar ID unico
const articleId = Date.now();

return [{
  json: {
    CONFIG,
    product,
    articleType,
    heroImage,
    colonias,
    zonasIndustriales,
    articleId,
    articleNumber: state.articleCount
  }
}];

const response = $input.first().json;
const data = $('Constructor Prompt').first().json;
const p = data.product;
const t = data.articleType;
const heroImage = data.heroImage;
const articleId = data.articleId;
const colonias = data.colonias;

// Parsear respuesta de ChatGPT
let content;
try {
  let raw = response.choices[0].message.content
    .replace(/```json\n?/gi, '')
    .replace(/```\n?/gi, '')
    .trim();
  content = JSON.parse(raw);
} catch(e) {
  throw new Error('Error parseando JSON: ' + e.message);
}

// Validar campos requeridos
if (!content.titulo || content.titulo.length < 20) {
  throw new Error('Titulo invalido o muy corto: ' + (content.titulo || 'VACIO'));
}
if (!content.contenidoHTML || content.contenidoHTML.length < 500) {
  throw new Error('Contenido HTML invalido o muy corto');
}

// Validar longitud de meta description
if (content.metaDescripcion && (content.metaDescripcion.length < 140 || content.metaDescripcion.length > 165)) {
  console.log('ADVERTENCIA: Meta description fuera de rango (150-160 chars):', content.metaDescripcion.length);
}

// Aplicar valores por defecto
content.metaDescripcion = content.metaDescripcion || content.metaDescription || `Guia profesional de ${p.name} con MANEXT en CDMX. 80 años de experiencia. Cotizacion gratis.`;
content.metaKeywords = content.metaKeywords || p.keywords.join(', ') + ', MANEXT, CDMX';
content.breadcrumbText = content.breadcrumbText || content.titulo.substring(0, 25);
content.tiempoLectura = content.tiempoLectura || '10 min';
content.faqSchema = content.faqSchema || [];
content.tags = content.tags || p.keywords.slice(0, 5);
content.enlacesInternos = content.enlacesInternos || p.relatedServices.map(s => s.url);
content.cta = content.cta || { texto: 'Solicita cotizacion', enlace: '/contacto.html' };

// Generar slug SEO optimizado
function generateSlug(text) {
  if (!text || typeof text !== 'string') return null;
  return text
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/[^a-z0-9\s-]/g, '')
    .replace(/\s+/g, '-')
    .replace(/-+/g, '-')
    .replace(/^-+|-+$/g, '')
    .substring(0, 55);
}

let slug = content.slug ? generateSlug(content.slug) : generateSlug(content.titulo);

// Asegurar keywords relevantes en slug
const relevantKeywords = ['extintor', 'extintores', 'mantenimiento', 'recarga', 'prueba', 'capacitacion', 'nom'];
const hasRelevantKeyword = relevantKeywords.some(kw => slug.includes(kw));
if (!hasRelevantKeyword) {
  const keyword = p.keywords[0].split(' ')[0].toLowerCase()
    .normalize('NFD').replace(/[\u0300-\u036f]/g, '');
  slug = keyword + '-' + slug;
}

slug = slug.substring(0, 60);

console.log('=== ARTICULO GENERADO ===');
console.log('TITULO:', content.titulo, '(' + content.titulo.length + ' chars)');
console.log('SLUG:', slug);
console.log('META:', content.metaDescripcion.length, 'chars');
console.log('CONTENIDO:', content.contenidoHTML.length, 'chars');
console.log('FAQs:', content.faqSchema.length);

// Construir FAQ Schema JSON-LD
const faqSchemaJSON = JSON.stringify({
  '@context': 'https://schema.org',
  '@type': 'FAQPage',
  'mainEntity': content.faqSchema.map(f => ({
    '@type': 'Question',
    'name': f.pregunta,
    'acceptedAnswer': {
      '@type': 'Answer',
      'text': f.respuesta
    }
  }))
});

return [{
