    content,
    slug,
    articleId,
    heroImage,
    faqSchemaJSON
  }
}];

const data = $('Validar Respuesta').first().json;
const templateResp = $input.first().json;
const c = data.content;
const p = data.product;
const slug = data.slug;
const heroImage = data.heroImage;
const faqSchemaJSON = data.faqSchemaJSON;

// Decodificar template
let template;
try {
  template = Buffer.from(templateResp.content, 'base64').toString('utf8');
} catch(e) {
  throw new Error('Error decodificando template: ' + e.message);
}

// Fecha actual
const today = new Date().toISOString().split('T')[0];

// Reemplazar todas las variables
let html = template
  .replace(/\{\{TITULO\}\}/g, c.titulo)
  .replace(/\{\{SLUG\}\}/g, slug)
  .replace(/\{\{META_DESCRIPTION\}\}/g, c.metaDescription)
  .replace(/\{\{META_KEYWORDS\}\}/g, c.metaKeywords)
  .replace(/\{\{CATEGORIA\}\}/g, c.categoria || p.categoryName)
  .replace(/\{\{CATEGORIA_SLUG\}\}/g, c.categoriaSlug || p.category)
  .replace(/\{\{IMAGEN_PRINCIPAL\}\}/g, heroImage)
  .replace(/\{\{IMAGEN_ALT\}\}/g, c.titulo + ' - MANEXT CDMX')
  .replace(/\{\{BREADCRUMB_TEXT\}\}/g, c.breadcrumbText)
  .replace(/\{\{TIEMPO_LECTURA\}\}/g, c.tiempoLectura)
  .replace(/\{\{FECHA_PUBLICACION\}\}/g, today)
  .replace(/\{\{CONTENIDO\}\}/g, c.contenidoHTML)
  .replace(/\{\{FAQ_SCHEMA\}\}/g, faqSchemaJSON);

// Convertir a Base64
const htmlBase64 = Buffer.from(html).toString('base64');

return [{
  json: {
    ...data,
    articleHtml: html,
    articleHtmlBase64: htmlBase64,
    slug,
    fechaPublicacion: today
  }
}];

