const data = $('Construir Card').first().json;
const articleEntry = data.articleEntry;
const slug = data.slug;
const gh = $input.first().json;

if (!slug || slug.length < 5) {
  throw new Error('Slug invalido antes de guardar: ' + slug);
}

if (!gh.content) throw new Error('articles.json no encontrado');

// Decodificar JSON actual
let articlesJson;
try {
  const content = Buffer.from(gh.content, 'base64').toString('utf8');
  articlesJson = JSON.parse(content);
} catch(e) {
  throw new Error('Error parseando articles.json: ' + e.message);
}

// Validar estructura
if (!articlesJson.articles || !Array.isArray(articlesJson.articles)) {
  // Si tiene estructura diferente con 'articulos' en lugar de 'articles'
  if (articlesJson.articulos && Array.isArray(articlesJson.articulos)) {
    articlesJson.articles = articlesJson.articulos;
  } else {
    articlesJson.articles = [];
  }
}

// Verificar duplicados
const exists = articlesJson.articles.some(a => a.slug === slug || a.id === slug);
if (exists) {
  throw new Error('Articulo con slug "' + slug + '" ya existe');
}

// Agregar al inicio del array articles
articlesJson.articles.unshift(articleEntry);

// Convertir a Base64
const newJSON = JSON.stringify(articlesJson, null, 2);
const newBase64 = Buffer.from(newJSON).toString('base64');

return [{
  json: {
    slug,
    newArticlesBase64: newBase64,
    sha: gh.sha,
    articleTitle: data.content.titulo,
    articleEntry,
    totalArticles: articlesJson.articles.length
  }
}];

